mapper.update:
update file set
folderId=#{folderId},name=#{name},picture=#{picture}
content=#{content},status=#{status}
where fileId=#{fileId}

update file set folderId
= #{folderId} where fileId = #{fileId} and userId =
#{userId}

mapper.select:
select * from file where userId = #{userId} and type=4
limit
#{offset},#{rows}
